<?php/** * Block template file: ACF/other-block/list-with-image.php * * List With Image Block Template. * * @param array $block The block settings and attributes. * @param string $content The block inner HTML (empty). * @param bool $is_preview True during AJAX preview. * @param (int|string) $post_id The post ID this block is saved to. */// Create id attribute allowing for custom "anchor" value.$id = get_field('anchor');if (!empty($block['anchor'])) {  $id = $block['anchor'];}// Create class attribute allowing for custom "className" and "align" values.$classes = '';if (!empty($block['className'])) {  $classes .= ' ' . $block['className'];}if (!empty($block['align'])) {  $classes .= ' align' . $block['align'];}?><section    class="corporate__11 <?php echo esc_attr($classes); ?>" <?php echo(!empty($id) ? 'id="' . esc_attr($id) . '"' : '') ?>>  <div class="container">    <div class="row">      <div class="col m12 s12 l12 xl12">        <p class="sub"><?php the_field('sub_title'); ?></p>        <h2 class="title"><?php the_field('title'); ?></h2>      </div>      <div class="col m12 s12 l6 xl6">        <?php        if (have_rows('list')) : ?>          <ul class="list">            <?php            $i = 1;            while (have_rows('list')) : the_row(); ?>              <li class="item"><span class="number"><?php echo $i; ?></span><?php the_sub_field('text'); ?></li>              <?php              $i++;            endwhile; ?>          </ul>        <?php        endif; ?>        <?php $link = get_field('link'); ?>        <?php        if ($link) : ?>          <a class="bundle" href="<?php echo esc_url($link['url']); ?>"             target="<?php echo esc_attr($link['target']); ?>"><?php echo esc_html($link['title']); ?></a>        <?php        endif; ?>      </div>      <div class="col m12 s12 l6 xl6">        <div class="img">          <?php $image = get_field('image');          if ($image) : ?>            <img src="<?php echo esc_url($image['url']); ?>" alt="<?php echo esc_attr($image['alt']); ?>"/>          <?php          endif; ?>        </div>      </div>    </div>  </div></section>